	private void DeserializeGameDate(IEventBase a_Event)
	{

		EventDeserializeGameEssentialData data = a_Event as EventDeserializeGameEssentialData;
		if(data == null)
		{
			Debug.LogError("[EssentialDataManager] EventDeserializeGameEssentialData error");
			return;
		}

        m_jsonObject = new JSONObject(data.StrJsonData);
        Debug.Log("[EssentialDataManager] Message From BaseApp: " + m_jsonObject.Print(true));


		if (m_jsonObject.HasField("matchId"))
		{
			m_strMatchId = m_jsonObject.GetField("matchId").str;
		}
		else
		{
			Debug.LogError("[EssentialDataManager] matchId NOT found");
		}

	
		{
			Debug.LogError("[EssentialDataManager] DifficultyState NOT found");
		}

		
	}

	private void SerializeGameData()
	{
		m_jsonObject.Print(true);


		if (m_jsonObject.HasField("RoomName"))
		{
			m_jsonObject.SetField("RoomName", RoomName);
		}
		else
		{
			m_jsonObject.AddField("RoomName", RoomName);
		}

		if (m_jsonObject.HasField("RoomID"))
		{
			m_jsonObject.SetField("RoomID", RoomID);
		}
		else
		{
			m_jsonObject.AddField("RoomID", RoomID);
		}



		//if (m_jsonObject.HasField("TimeStamp"))
